mapper.select:
select

from collect
where blog_id = #{blogId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
limit 1

mapper.delete:
delete from collect
where blog_id = #{blogId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
limit 1

mapper.insert:
insert into collect (blog_id, collect_time, collect_corpus_id
  )
values (#{blogId,jdbcType=INTEGER}, #{collectTime,jdbcType=TIMESTAMP}, #{collectCorpusId,jdbcType=INTEGER}
  )

mapper.update:
update collect

  
    collect_time = #{collectTime,jdbcType=TIMESTAMP},
  
  
    collect_corpus_id = #{collectCorpusId,jdbcType=INTEGER},
  

where blog_id = #{blogId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}

update collect
set collect_time = #{collectTime,jdbcType=TIMESTAMP},
  collect_corpus_id = #{collectCorpusId,jdbcType=INTEGER}
where blog_id = #{blogId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
limit 1
