mapper.select:
select count(*)
from subscription
where sub_id = #{userId}

select count(*)
from subscription
where fan_id = #{userId}

select a.id, a.avatar, a.username, a.email, a.gender, a.person_msg
from user a,
     subscription b
where a.id = b.sub_id
  and b.fan_id = #{fanId}

select b.fan_id as id, a.avatar, a.username, a.email, a.gender, a.person_msg
from user a,
     subscription b
where a.id = b.fan_id
  and b.sub_id = #{userId}
