mapper.select:
select

from subscription
where sub_id = #{subId,jdbcType=INTEGER}
  and fan_id = #{fanId,jdbcType=INTEGER}

mapper.delete:
delete from subscription
where sub_id = #{subId,jdbcType=INTEGER}
  and fan_id = #{fanId,jdbcType=INTEGER}
limit 1

mapper.insert:
insert into subscription (fan_id, sub_time, receive_msg
  )
values (#{fanId,jdbcType=INTEGER}, #{subTime,jdbcType=TIMESTAMP}, #{receiveMsg,jdbcType=BOOLEAN}
  )

mapper.update:
update subscription

  
    sub_time = #{subTime,jdbcType=TIMESTAMP},
  
  
    receive_msg = #{receiveMsg,jdbcType=BOOLEAN},
  

where sub_id = #{subId,jdbcType=INTEGER}
  and fan_id = #{fanId,jdbcType=INTEGER}
limit 1

update subscription
set sub_time = #{subTime,jdbcType=TIMESTAMP},
  receive_msg = #{receiveMsg,jdbcType=BOOLEAN}
where sub_id = #{subId,jdbcType=INTEGER}
  and fan_id = #{fanId,jdbcType=INTEGER}
limit 1
